const axios = require('axios');
const { Parser } = require('htmlparser2');
const { URL } = require('url');
const { Transform } = require('stream');

class PageAnalysis {
  constructor(url) {
    this.url = url;
    this.internalLinks = new Map(); // Map<url, {anchorText: string, context: string}>
    this.externalLinks = new Map();
    this.incomingLinks = new Set();
  }
}

async function streamParsePage(url, domain) {
  const pageAnalysis = new PageAnalysis(url);

  try {
    const response = await axios.get(url, {
      timeout: 5000,
      responseType: 'stream',
      headers: {
        'User-Agent': 'Mozilla/5.0 (compatible; CustomBot/1.0)',
      }
    });

    return new Promise((resolve, reject) => {
      let currentTag = null;
      let currentHref = null;
      let currentText = '';
      let isCollectingText = false;

      const parser = new Parser({
        onopentag(name, attributes) {
          if (name === 'a' && attributes.href) {
            currentTag = 'a';
            currentHref = attributes.href;
            currentText = '';
            isCollectingText = true;
          }
        },

        ontext(text) {
          if (isCollectingText) {
            currentText += text.trim() + ' ';
          }
        },

        onclosetag(tagname) {
          if (tagname === 'a' && currentHref) {
            try {
              let href = currentHref;

              if (href.startsWith('/')) {
                href = new URL(href, domain).href;
              }

              href = new URL(href).href;

              const linkData = {
                anchorText: currentText.trim(),
                originalHref: currentHref
              };

              if (href.startsWith(domain)) {
                pageAnalysis.internalLinks.set(href, linkData);
              } else {
                pageAnalysis.externalLinks.set(href, linkData);
              }
            } catch (e) {
              // Skip invalid URLs
            }

            currentTag = null;
            currentHref = null;
            currentText = '';
            isCollectingText = false;
          }
        },

        onerror: reject,
        onend: () => resolve(pageAnalysis)
      });

      const transformStream = new Transform({
        transform(chunk, encoding, callback) {
          parser.write(chunk);
          callback();
        }
      });

      response.data
        .pipe(transformStream)
        .on('finish', () => parser.end())
        .on('error', reject);
    });
  } catch (error) {
    console.error(`Error analyzing ${url}: ${error.message}`);
    return null;
  }
}



async function processBatchWithPrefetch(urls, domain, pageAnalyses, prefetchSize = 10) {
  const nextBatchUrls = Array.from(urls).slice(prefetchSize, prefetchSize * 2);
  const prefetchPromises = nextBatchUrls.map(url => 
    axios.get(url, {
      timeout: 5000,
      responseType: 'stream',
      headers: {
        'User-Agent': 'Mozilla/5.0 (compatible; CustomBot/1.0)',
      }
    }).catch(() => null)
  );

  const currentBatchPromises = urls.slice(0, prefetchSize).map(url => 
    streamParsePage(url, domain)
  );

  const results = await Promise.allSettled(currentBatchPromises);

  results.forEach((result, index) => {
    if (result.status === 'fulfilled' && result.value) {
      pageAnalyses.set(urls[index], result.value);
    }
  });

  Promise.allSettled(prefetchPromises);

  return urls.filter((url, index) => 
    results[index]?.status === 'fulfilled' && results[index]?.value
  );
}

async function analyzeDomain(domain, batchSize = 10) {
  const visited = new Set();
  const toVisit = new Set([domain]);
  const pageAnalyses = new Map();
  const urlQueue = [[domain, 0]];
  const maxDepth = 5;

  while (urlQueue.length > 0) {
    const currentBatch = [];
    while (currentBatch.length < batchSize && urlQueue.length > 0) {
      const [url, depth] = urlQueue.shift();
      if (depth <= maxDepth) {
        currentBatch.push(url);
        toVisit.delete(url);
      }
    }

    if (currentBatch.length === 0) break;

    const successfulUrls = await processBatchWithPrefetch(currentBatch, domain, pageAnalyses);

    successfulUrls.forEach(url => {
      visited.add(url);

      const analysis = pageAnalyses.get(url);
      if (!analysis) return;

      const currentDepth = urlQueue.find(([u]) => u === url)?.[1] || 0;

      analysis.internalLinks.forEach((_, href) => {
        if (!visited.has(href) && !toVisit.has(href)) {
          urlQueue.push([href, currentDepth + 1]);
          toVisit.add(href);
        }

        if (!pageAnalyses.has(href)) {
          pageAnalyses.set(href, new PageAnalysis(href));
        }
        pageAnalyses.get(href).incomingLinks.add(url);
      });
    });
  }

  return pageAnalyses;
}

function generateAnalysisReport(pageAnalyses) {
  const report = [];

  for (const [url, analysis] of pageAnalyses) {
    report.push({
      url,
      internalLinks: Array.from(analysis.internalLinks, ([url, data]) => ({
        url,
        anchorText: data.anchorText,
        originalHref: data.originalHref
      })),
      externalLinks: Array.from(analysis.externalLinks, ([url, data]) => ({
        url,
        anchorText: data.anchorText,
        originalHref: data.originalHref
      })),
      incomingLinks: Array.from(analysis.incomingLinks).map(incomingUrl => {
        const incomingPage = pageAnalyses.get(incomingUrl);
        const linkData = incomingPage?.internalLinks.get(url);
        return {
          url: incomingUrl,
          anchorText: linkData?.anchorText || '',
          originalHref: linkData?.originalHref || ''
        };
      })
    });
  }

  return report;
}


function generateChartData(pageAnalyses) {
  const nodes = [];
  const links = [];
  const nodeMap = new Map();

  let nodeId = 0;
  for (const url of pageAnalyses.keys()) {
    nodes.push({
      id: nodeId,
      label: new URL(url).pathname || '/',
      url: url
    });
    nodeMap.set(url, nodeId);
    nodeId++;
  }

  for (const [url, analysis] of pageAnalyses) {
    const sourceId = nodeMap.get(url);

    for (const targetUrl of analysis.internalLinks.keys()) {
      const targetId = nodeMap.get(targetUrl);
      if (targetId !== undefined) {
        links.push({
          source: sourceId,
          target: targetId
        });
      }
    }
  }

  return { nodes, links };
}

function generateSitemap(urls) {
  return `<?xml version="1.0" encoding="UTF-8"?>
  <urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
    ${urls.map(url => `
      <url>
        <loc>${url}</loc>
        <lastmod>${new Date().toISOString()}</lastmod>
        <priority>0.5</priority>
      </url>
    `).join('')}
  </urlset>`;
}


