async function main(domain) {
  try {
    console.time('Crawl Duration');
    const pageAnalyses = await analyzeDomain(domain);
    console.timeEnd('Crawl Duration');

    const report = generateAnalysisReport(pageAnalyses);
    const chartData = generateChartData(pageAnalyses);
    const visualization = generateVisualizationHtml({ chartData });

    return {
      sitemap: generateSitemap(Array.from(pageAnalyses.keys())),
      report,
      visualization
    };
  } catch (error) {
    console.error('Error in main:', error);
    throw error;
  }
}

