async function analyzeDomain(domain, batchSize = 10) {
  const visited = new Set();
  const toVisit = new Set([domain]);
  const pageAnalyses = new Map();
  const urlQueue = [[domain, 0]];
  const maxDepth = 5;

  while (urlQueue.length > 0) {
    const currentBatch = [];
    while (currentBatch.length < batchSize && urlQueue.length > 0) {
      const [url, depth] = urlQueue.shift();
      if (depth <= maxDepth) {
        currentBatch.push(url);
        toVisit.delete(url);
      }
    }

    if (currentBatch.length === 0) break;

    const successfulUrls = await processBatchWithPrefetch(currentBatch, domain, pageAnalyses);

    successfulUrls.forEach(url => {
      visited.add(url);

      const analysis = pageAnalyses.get(url);
      if (!analysis) return;

      const currentDepth = urlQueue.find(([u]) => u === url)?.[1] || 0;

      analysis.internalLinks.forEach((_, href) => {
        if (!visited.has(href) && !toVisit.has(href)) {
          urlQueue.push([href, currentDepth + 1]);
          toVisit.add(href);
        }

        if (!pageAnalyses.has(href)) {
          pageAnalyses.set(href, new PageAnalysis(href));
        }
        pageAnalyses.get(href).incomingLinks.add(url);
      });
    });
  }

  return pageAnalyses;
}

