      .velocityDecay(0.6)
      .force("link", d3.forceLink(data.links)
        .id(d => d.id)
        .distance(80)
        .strength(0.5))
      .force("charge", d3.forceManyBody()
        .strength(-200)
        .distanceMax(200))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .force("collision", d3.forceCollide().radius(40).strength(0.9))
      .force("x", d3.forceX(width / 2).strength(0.1))
      .force("y", d3.forceY(height / 2).strength(0.1));

    const svg = d3.select("#chart")
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    const g = svg.append("g");
    svg.call(d3.zoom()
      .scaleExtent([0.1, 4])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);
      }));

    const tooltip = d3.select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    const link = g.append("g")
      .selectAll("line")
      .data(data.links)
      .join("line")
      .attr("class", "link");

    const node = g.append("g")
      .selectAll("circle")
      .data(data.nodes)
      .join("circle")
      .attr("class", "node")
      .attr("r", d => Math.max(6, Math.min(12, 6 + Math.sqrt(d.incomingLinks + d.outgoingLinks))))
      .attr("fill", d => d3.schemeCategory10[d.id % 10])
      .on("click", highlightChain)
      .on("mouseover", showTooltip)
      .on("mouseout", hideTooltip)
      .call(drag(simulation));

    const label = g.append("g")
      .selectAll("text")
      .data(data.nodes)
      .join("text")
      .attr("class", "node-label")
      .text(d => d.label)
      .attr("x", 12)
      .attr("y", 3);

    function showTooltip(event, d) {
      tooltip.transition()
        .duration(200)
        .style("opacity", .9);
      tooltip.html(
        \`<strong>\${d.url}</strong>
         <div class="stats">
           Incoming links: \${d.incomingLinks}<br>
           Outgoing links: \${d.outgoingLinks}
         </div>\`
      )
        .style("left", (event.pageX + 10) + "px")
        .style("top", (event.pageY - 10) + "px");
    }

    function hideTooltip() {
      tooltip.transition()
        .duration(500)
        .style("opacity", 0);
    }

    function highlightChain(event, d) {
      // Find connected nodes and links
      const connectedLinks = data.links.filter(l => 
        l.source.id === d.id || l.source === d.id || 
        l.target.id === d.id || l.target === d.id
      );

      const connectedNodes = new Set();
      connectedLinks.forEach(l => {
        connectedNodes.add(l.source.id || l.source);
        connectedNodes.add(l.target.id || l.target);
      });

      // Update visual states
      link
        .classed("highlighted", l => connectedLinks.includes(l))
        .classed("dimmed", l => !connectedLinks.includes(l));

      node
        .classed("dimmed", n => !connectedNodes.has(n.id));

      label
        .classed("dimmed", n => !connectedNodes.has(n.id));
    }

    // Reset highlight when clicking on background
    svg.on("click", (event) => {
      if (event.target.tagName === "svg") {
        link
          .classed("highlighted", false)
          .classed("dimmed", false);
        node
          .classed("dimmed", false);
        label
          .classed("dimmed", false);
      }
    });

    function drag(simulation) {
      function dragstarted(event) {
        if (!event.active) simulation.alphaTarget(0.1).restart();
        event.subject.fx = event.subject.x;
        event.subject.fy = event.subject.y;
      }

      function dragged(event) {
        event.subject.fx = event.x;
        event.subject.fy = event.y;
      }

      function dragended(event) {
        if (!event.active) simulation.alphaTarget(0);
        event.subject.fx = event.x;
        event.subject.fy = event.y;
      }

      return d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended);
    }

    // Initial positions based on grid
    simulation.nodes(data.nodes).tick(100);

    simulation.on("tick", () => {
      link
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      node
        .attr("cx", d => d.x)
        .attr("cy", d => d.y);

      label
        .attr("x", d => d.x + 12)
        .attr("y", d => d.y + 3);
    });
  </script>
</body>
</html>`;
}

