const cheerio = require('cheerio');
const axios = require('axios');

return Promise.all(items.map(async (item) => {
  const url = item.json.url;

  if (!url) {
    return { json: { error: "No URL provided" } };
  }

  try {
    // Fetch the HTML content from the URL
    const response = await axios.get(url);
    const html = response.data;

    // Load HTML into Cheerio
    const $ = cheerio.load(html);

    // Extract the title tag
    const title = $('title').text() || "No title found";

    // Extract the meta description
    const metaDescription = $('meta[name="description"]').attr('content') || "No description found";

    // Extract the canonical URL
    const canonical = $('link[rel="canonical"]').attr('href') || "No canonical URL found";

    // Extract all headings (H1, H2, H3, etc.)
    const headings = [];
    $('h1, h2, h3, h4, h5, h6').each((i, el) => {
      headings.push({
        level: el.name,
        text: $(el).text().trim()
      });
    });

    // Extract internal and external links with anchor texts
    const internalLinks = [];
    const externalLinks = [];
    $('a').each((i, el) => {
      const href = $(el).attr('href');
      const anchorText = $(el).text().trim();

      if (href) {
        if (href.startsWith('/') || href.includes(url)) {
          internalLinks.push({ href, anchorText: anchorText || "No anchor text" });
        } else {
          externalLinks.push({ href, anchorText: anchorText || "No anchor text" });
        }
      }
    });

    // Extract robots meta tag
    const robotsMeta = $('meta[name="robots"]').attr('content') || "No robots meta found";

    // Extract Open Graph tags
    const ogTags = {};
    $('meta[property^="og:"]').each((i, el) => {
      const property = $(el).attr('property');
      const content = $(el).attr('content');
      if (property && content) {
        ogTags[property.replace('og:', '')] = content;
      }
    });

    // Extract structured data (JSON-LD)
    const structuredData = [];
    $('script[type="application/ld+json"]').each((i, el) => {
      try {
        const json = JSON.parse($(el).html());
        structuredData.push(json);
      } catch (error) {
        // Ignore invalid JSON
      }
    });

    // Extract image details
    const images = [];
    $('img').each((i, el) => {
      images.push({
        src: $(el).attr('src') || "No source",
        alt: $(el).attr('alt') || "No alt text",
        width: $(el).attr('width') || "No width",
        height: $(el).attr('height') || "No height"
      });
    });

    // Analyze viewport meta tag
    const viewportMeta = $('meta[name="viewport"]').attr('content') || "No viewport meta tag";

    // Calculate word count
    const wordCount = $('body').text().split(/\s+/).filter(word => word.length > 0).length;

    // Check for missing alt attributes
    const imagesWithoutAlt = images.filter(img => img.alt === "No alt text").length;

    // Return the structured result
    return {
      json: {
        url,
        title,
        metaDescription,
        canonical,
        headings,
        internalLinks,
        externalLinks,
        robotsMeta,
        openGraph: ogTags,
        structuredData,
        images,
        imageAnalysis: {
          totalImages: images.length,
          imagesWithoutAlt
        },
        viewportMeta,
        wordCount
      }
    };

  } catch (error) {
    return { json: { error: `Failed to fetch URL: ${url}`, details: error.message } };
  }
}));

