// Technical Analysis and geneerate HTML Beautified

const cheerio = require('cheerio');
const axios = require('axios');

return Promise.all(items.map(async (item) => {
  const url = item.json.url;

  if (!url) {
    return { json: { error: "No URL provided" } };
  }

  try {
    const response = await axios.get(url);
    const html = response.data;
    const $ = cheerio.load(html);

    // Improved title extraction and cleaning
    let title = $('title').text() || "No title found";
    title = title
      .replace(/\s*Icon\w+/g, '')  // Remove Icon references
      .replace(/([a-z])([A-Z])/g, '$1 $2')  // Add space between camelCase
      .replace(/\s+/g, ' ')  // Remove extra spaces
      .split(/\s+/)  // Split into words
      .filter((word, index, arr) => arr.indexOf(word) === index)  // Remove duplicates
      .join(' ')  // Join back with spaces
      .trim();  // Clean up extra spaces

    const metaDescription = $('meta[name="description"]').attr('content') || "No description found";
    const canonical = $('link[rel="canonical"]').attr('href') || "No canonical URL found";
    const headings = [];
    $('h1, h2, h3, h4, h5, h6').each((i, el) => {
      headings.push({
        level: el.name,
        text: $(el).text().trim()
      });
    });
    const internalLinks = [];
    const externalLinks = [];
    $('a').each((i, el) => {
      const href = $(el).attr('href');
      const anchorText = $(el).text().trim();
      if (href) {
        if (href.startsWith('/') || href.includes(url)) {
          internalLinks.push({ href, anchorText: anchorText || "No anchor text" });
        } else {
          externalLinks.push({ href, anchorText: anchorText || "No anchor text" });
        }
      }
    });

    const robotsMeta = $('meta[name="robots"]').attr('content') || "No robots meta found";
    const ogTags = {};
    $('meta[property^="og:"]').each((i, el) => {
      const property = $(el).attr('property');
      const content = $(el).attr('content');
      if (property && content) {
        ogTags[property.replace('og:', '')] = content;
      }
    });

    const structuredData = [];
    $('script[type="application/ld+json"]').each((i, el) => {
      try {
        const json = JSON.parse($(el).html());
        structuredData.push(json);
      } catch (error) {
        // Ignore invalid JSON
      }
    });

    const images = [];
    $('img').each((i, el) => {
      images.push({
        src: $(el).attr('src') || "No source",
        alt: $(el).attr('alt') || "No alt text",
        width: $(el).attr('width') || "No width",
        height: $(el).attr('height') || "No height"
      });
    });

    const viewportMeta = $('meta[name="viewport"]').attr('content') || "No viewport meta tag";
    const wordCount = $('body').text().split(/\s+/).filter(word => word.length > 0).length;
    const imagesWithoutAlt = images.filter(img => img.alt === "No alt text").length;

    const htmlContent = `
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <style>
        :root {
          --primary-color: #2563eb;
          --secondary-color: #1e40af;
          --text-color: #1f2937;
          --border-color: #e5e7eb;
          --background-color: #f9fafb;
          --success-color: #059669;
        }

        body {
          font-family: system-ui, -apple-system, sans-serif;
          margin: 0;
          padding: 0;
          background-color: var(--background-color);
          color: var(--text-color);
          line-height: 1.5;
        }

        .container {
          max-width: 1200px;
          margin: 2rem auto;
          padding: 0 1.5rem;
        }

        .header {
          background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
          color: white;
          padding: 2rem;
          border-radius: 12px;
          margin-bottom: 2rem;
          box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1);
        }

        .header h1 {
          margin: 0;
          font-size: 1.875rem;
          font-weight: 600;
          word-wrap: break-word;
          line-height: 1.2;
        }

        .section {
          background: white;
          border-radius: 8px;
          padding: 1.5rem;
          margin-bottom: 1.5rem;
          box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1);
        }

        .section h2 {
          color: var(--primary-color);
          font-size: 1.25rem;
          margin-top: 0;
          margin-bottom: 1rem;
          padding-bottom: 0.5rem;
          border-bottom: 2px solid var(--border-color);
        }

        .section h3 {
          color: var(--secondary-color);
          font-size: 1.125rem;
          margin: 1.5rem 0 1rem;
        }

        table {
          width: 100%;
          border-collapse: collapse;
          margin: 1rem 0;
          font-size: 0.875rem;
        }

        th, td {
          padding: 0.75rem 1rem;
          text-align: left;
          border: 1px solid var(--border-color);
          word-break: break-word;
        }

        th {
          background-color: var(--background-color);
          font-weight: 600;
        }

        tr:hover {
          background-color: var(--background-color);
        }

        .image-table img {
          max-width: 120px;
          border-radius: 4px;
          object-fit: cover;
        }

        ul {
          list-style-type: none;
          padding: 0;
          margin: 0;
        }

        ul li {
          padding: 0.5rem 0;
          border-bottom: 1px solid var(--border-color);
        }

        ul li:last-child {
          border-bottom: none;
        }

        pre {
          background-color: var(--background-color);
          padding: 1rem;
          border-radius: 6px;
          overflow-x: auto;
          font-size: 0.875rem;
          white-space: pre-wrap;
          word-wrap: break-word;
        }

        .stats-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 1rem;
          margin-bottom: 1rem;
        }

        .stat-card {
          background: var(--background-color);
          padding: 1rem;
          border-radius: 6px;
          text-align: center;
        }

        .stat-card .value {
          font-size: 1.5rem;
          font-weight: 600;
          color: var(--primary-color);
        }

        .stat-card .label {
          font-size: 0.875rem;
          color: var(--text-color);
          margin-top: 0.5rem;
        }

        @media (max-width: 768px) {
          .container {
            padding: 0 1rem;
          }

          .header {
            padding: 1.5rem;
          }

          table {
            display: block;
            overflow-x: auto;
          }

          .header h1 {
            font-size: 1.5rem;
          }
        }
      </style>
    </head>
    <body>
      <div class="container">
        <div class="header">
          <h1>Technical SEO Analysis: ${title}</h1>
        </div>

        <div class="section">
          <h2>Key Metrics</h2>
          <div class="stats-grid">
            <div class="stat-card">
              <div class="value">${wordCount}</div>
              <div class="label">Word Count</div>
            </div>
            <div class="stat-card">
              <div class="value">${imagesWithoutAlt}</div>
              <div class="label">Images Missing Alt Text</div>
            </div>
            <div class="stat-card">
              <div class="value">${internalLinks.length}</div>
              <div class="label">Internal Links</div>
            </div>
            <div class="stat-card">
              <div class="value">${externalLinks.length}</div>
              <div class="label">External Links</div>
            </div>
          </div>
        </div>

        <div class="section">
          <h2>Meta Information</h2>
          <table>
            <tr><th>Title</th><td>${title}</td></tr>
            <tr><th>Meta Description</th><td>${metaDescription}</td></tr>
            <tr><th>Canonical URL</th><td>${canonical}</td></tr>
            <tr><th>Robots Meta</th><td>${robotsMeta}</td></tr>
            <tr><th>Viewport Meta</th><td>${viewportMeta}</td></tr>
          </table>
        </div>

        <div class="section">
          <h2>Content Structure</h2>
          <h3>Headings Hierarchy</h3>
          <ul>
            ${headings.map(h => `<li><strong>${h.level}</strong>: ${h.text}</li>`).join('')}
          </ul>
        </div>

        <div class="section">
          <h2>Link Analysis</h2>
          <h3>Internal Links</h3>
          <table>
            <tr><th>Anchor Text</th><th>URL</th></tr>
            ${internalLinks.map(link => `<tr><td>${link.anchorText}</td><td><a href="${link.href}">${link.href}</a></td></tr>`).join('')}
          </table>

          <h3>External Links</h3>
          <table>
            <tr><th>Anchor Text</th><th>URL</th></tr>
            ${externalLinks.map(link => `<tr><td>${link.anchorText}</td><td><a href="${link.href}">${link.href}</a></td></tr>`).join('')}
          </table>
        </div>

        <div class="section">
          <h2>Open Graph Tags</h2>
          <table>
            ${Object.entries(ogTags).map(([key, value]) => `<tr><th>${key}</th><td>${value}</td></tr>`).join('')}
          </table>
        </div>

        <div class="section">
          <h2>Image Analysis</h2>
          <table class="image-table">
            <tr><th>Image</th><th>Alt Text</th><th>Dimensions</th></tr>
            ${images.map(img => `
              <tr>
                <td><img src="${img.src}" alt="${img.alt}" width="100"></td>
                <td>${img.alt}</td>
                <td>${img.width} Ã— ${img.height}</td>
              </tr>
            `).join('')}
          </table>
        </div>

        <div class="section">
          <h2>Structured Data</h2>
          <pre>${JSON.stringify(structuredData, null, 2)}</pre>
        </div>
      </div>
    </body>
    </html>
    `;

    return { json: { html: htmlContent } };

  } catch (error) {
    return { json: { error: `Failed to fetch URL: ${url}`, details: error.message } };
  }
}));
